package com.vipapp.appmark2.items;import com.vipapp.appmark2.project.Project;public class OnProjectEdited {    private int position;    private Project project;    public OnProjectEdited(Project project, int position) {        this.project = project;        this.position = position;    }    public Project getProject() {        return this.project;    }    public void setProject(Project project) {        this.project = project;    }    public int getPosition() {        return this.position;    }    public void setPosition(int position) {        this.position = position;    }}