package com.vipapp.appmark2.menu;import android.content.Context;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import com.vipapp.appmark2.R;import com.vipapp.appmark2.item.Image;import com.vipapp.appmark2.item.Item;import com.vipapp.appmark2.manager.GalleryManager;import com.vipapp.appmark2.util.ImageUtils;import com.vipapp.appmark2.widget.RecyclerView;import java.util.ArrayList;import androidx.annotation.NonNull;import static com.vipapp.appmark2.util.Const.GALLERY_LOADED;public class ImageMenu extends DefaultMenu<Image, ImageMenu.ImageHolder> {    private void getImages() {        new GalleryManager(-1).exec(result -> {            pushArray(result);            pushItem(new Item<>(GALLERY_LOADED, null));        });    }    public void bind(ImageHolder imageHolder, Image image, int i) {        imageHolder.image.setOnClickListener(view -> pushItem(new Item<>(0, image)));        ImageUtils.loadInto(image.getFile(), imageHolder.image);    }    @Override    public int getLayoutResource() {        return R.layout.image_default;    }    @Override    public ImageHolder getViewHolder(ViewGroup parent, int itemType) {        return new ImageHolder(inflate(parent));    }    public ArrayList<Image> list(Context context) {        getImages();        return null;    }    static class ImageHolder extends RecyclerView.ViewHolder{        public ImageView image;        public ImageHolder(@NonNull View itemView) {            super(itemView);            image = itemView.findViewById(R.id.image);        }    }}